import React, { useContext } from "react";
import { Link } from "react-router-dom";
import authContext from "./context/authContext";

import "../styles/Nav.css";

function Nav(props) {
  const [{ isLoggedIn }, dispatch] = useContext(authContext);
  const logOut = () => {
    dispatch({
      type: "LOGOUT"
    });
  };
  return (
    <header hidden={props.hidden}>
      <nav>
        <Link to="/" className="logo">
          <p id="logo-text">ClimateTree</p>
        </Link>
        <ul className="nav-links">
          <li>
            <Link to="/" className="underline-hover">
              HOME
            </Link>
          </li>
          <li>
            <Link to="/maps" className="underline-hover">
              MAP
            </Link>
          </li>
          <li>
            <Link to="/about" className="underline-hover">
              <span>ABOUT</span>
            </Link>
          </li>
          <li>
            {isLoggedIn ? (
              <Link to="/login" className="underline-hover">
                <span>PROFILE</span>
              </Link>
            ) : (
              <Link to="/" className="underline-hover">
                <span></span>
              </Link>
            )}
          </li>
          <li>
            {isLoggedIn ? (
              <Link to="/login" onClick={logOut} className="underline-hover">
                <span>LOGOUT</span>
              </Link>
            ) : (
              <Link to="/login" className="underline-hover">
                <span>LOGIN</span>
              </Link>
            )}
          </li>
        </ul>
        <div className="burger" onClick={navSlide}>
          <div className="line1"></div>
          <div className="line2"></div>
          <div className="line3"></div>
        </div>
      </nav>
    </header>
  );
}

Nav.defaultProps = {
  hidden: false
};

function navSlide() {
  document.querySelector(".nav-links").classList.toggle("nav-active");
  document.querySelectorAll(".nav-links li").forEach((link, index) => {
    if (link.style.animation) {
      link.style.animation = "";
    } else {
      link.style.animation = `navLinkFade 0.5s ease forwards ${index / 7 +
        0.2}s`;
    }
  });
  document.querySelector(".burger").classList.toggle("toggle");
}

export default Nav;
